#include <stdio.h>
#include <stdbool.h>
#include <mem.h>
#include <malloc.h>


struct Stack {
    char *stack;
    char *top, *base;
};

void stInitial(struct Stack *stack, int len) {
    stack->stack = (char *) calloc(1, len);
    stack->top = stack->stack;
    stack->base = stack->top;
}

void stPush(struct Stack *stack, char val) {
    *stack->top++ = val;
}

char stPop(struct Stack *stack) {
    if (stack->top != stack->base) {
        stack->top--;
    }
    return *stack->top;

}

bool isValid(char *s) {
    int len = strlen(s);
    if (len % 2 != 0) return false;

    bool flag = true;
    struct Stack stL;
    stInitial(&stL, len);

    while (*s) {
        switch (*s) {
            case '(':
            case '[':
            case '{':
                stPush(&stL, *s);
                break;
            case ')':
                if (stPop(&stL) != '(') return false;
                break;
            case ']':
                if (stPop(&stL) != '[')return false;
                break;
            case '}':
                if (stPop(&stL) != '{')return false;
                break;
            default:
                break;
        }
        s++;
    }

    if(stL.top!=stL.base) return false;


    return true;
}

